import React, {useState} from 'react';
import ScriptTag from 'react-script-tag';
import './FrontPage.css';
import { motion, AnimatePresence } from 'framer-motion';
import {FontAwesomeIcon} from'@fortawesome/react-fontawesome';
import { faChevronRight } from '@fortawesome/free-solid-svg-icons';
import {faFacebookSquare, faGithubSquare, faLinkedin, faTwitterSquare} from '@fortawesome/free-brands-svg-icons';
import { Link } from 'react-router-dom';
import {Button, Modal} from 'react-bootstrap';
import Resume from './img/resume.png';
import Download from './img/resume.docx';



const FrontPage = () => {
  const [hover, setHover] = useState(false);

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);


  const variantsContainer = {
    hidden: {
      opacity: 1
    },
    visible: {
      opacity: 1,
      transition: {
        delay: .1, duration: .1
      }
    },
    exit: {
      opacity: .8


    }

  }

  const hovering = e => {
    setHover(true);
  }

  const notHovering = e => {
    setHover(false);
  }

  particlesJS("particles-js", {
    "particles": {
      "number": {
        "value": 290,
        "density": {
          "enable": true,
          "value_area": 800
        }
      },
      "color": {
        "value": "#ffffff"
      },
      "shape": {
        "type": "triangle",
        "stroke": {
          "width": 0,
          "color": "#000000"
        },
        "polygon": {
          "nb_sides": 10
        },
        "image": {
          "src": "img/github.svg",
          "width": 100,
          "height": 100
        }
      },
      "opacity": {
        "value": 0.5,
        "random": false,
        "anim": {
          "enable": false,
          "speed": 1,
          "opacity_min": 0.1,
          "sync": false
        }
      },
      "size": {
        "value": 3,
        "random": true,
        "anim": {
          "enable": false,
          "speed": 40,
          "size_min": 0.1,
          "sync": false
        }
      },
      "line_linked": {
        "enable": true,
        "distance": 150,
        "color": "#ffffff",
        "opacity": 0.4,
        "width": 1
      },
      "move": {
        "enable": true,
        "speed": 6,
        "direction": "none",
        "random": true,
        "straight": false,
        "out_mode": "out",
        "bounce": true,
        "attract": {
          "enable": false,
          "rotateX": 600,
          "rotateY": 1200
        }
      }
    },
    "interactivity": {
      "detect_on": "window",
      "events": {
        "onhover": {
          "enable": true,
          "mode": "repulse"
        },
        "onclick": {
          "enable": true,
          "mode": "push"
        },
        "resize": true
      },
      "modes": {
        "grab": {
          "distance": 140,
          "line_linked": {
            "opacity": 1
          }
        },
        "bubble": {
          "distance": 400,
          "size": 100,
          "duration": 2,
          "opacity": 8,
          "speed": 3
        },
        "repulse": {
          "distance": 100,
          "duration": 0.4
        },
        "push": {
          "particles_nb": 4
        },
        "remove": {
          "particles_nb": 2
        }
      }
    },
    "retina_detect": true
  });


  return (
    <motion.div
    className='home-container'
    variants={variantsContainer}
    initial='hidden'
    animate='visible'
    type='tween'
    exit='exit'
    >
<section id="particles-js">
        <h1>Section 1</h1>
</section>
<section id="section2">
        <h1>Section 2</h1>
</section>
<section id="section3">
        <h1>Section 3</h1>
</section>
<section id="section4">
        <h1>Section 4</h1>
</section>
      <div className='resume-email'>
        <Button variant="secondary" onClick={handleShow}>
          Click for resume and email
        </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>My Email: blackwellj1040@gmail.com</Modal.Title>
          </Modal.Header>
          <Modal.Body><img className='resume' src={Resume} /></Modal.Body>
          <Modal.Footer>
            <a href={Download} download>Click to download</a>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>

          </Modal.Footer>
        </Modal>
      </div>

      <div>
        <div className='faIcons'>

          <a href='https://www.facebook.com/jamison.blackwell.3/'>{<FontAwesomeIcon className='list-icon' icon={faFacebookSquare}/> }</a>


          <a href='https://twitter.com/JamisonBlackw10'>{<FontAwesomeIcon className='list-icon' icon={faTwitterSquare}/> }</a>


          <a href='https://github.com/jblack186'>{<FontAwesomeIcon className='list-icon' icon={faGithubSquare}/> }</a>


          <a href='https://www.linkedin.com/in/jamison-blackwell/'>{<FontAwesomeIcon className='list-icon' icon={faLinkedin}/> }</a>


        </div>
      </div>
      <div className='home-content'>
        <h1>Hello, I'm <span class="name">Jamison Blackwell.</span> <p>I am a full-stack web developer.</p></h1>
        <p className='about-me'>ReactJS, NodeJS, HTML5, CSS3, JavaScript, LESS/SASS, Express, MongoDB, SQLite3, Knex, Hosting, Git Version Control</p>
        <p className='about-me'>If you don't see what your looking for there's a good chance it will be up there soon. Learning never ends.</p>
        <Link style={{ textDecoration: 'none' }}  to='dashboard' ><motion.div 
      onMouseOver={hovering}
      onMouseLeave={notHovering}
      whileHover={{scale: 1.1, textShadow: "0px 0px 8px rgb(255, 255, 255)", boxShadow: "0px 0px 8px rgb(255, 255, 255)"}}
      class="button-link">View my work </motion.div></Link>
    </div>
    <div className='arrow'>
      <p>To Work</p>
    <Link style={{ textDecoration: 'none' }}  to='dashboard' > <div >
    {<FontAwesomeIcon className='font-icon' icon={faChevronRight}/> }
    </div></Link>
    </div>

    </motion.div>
  )
}

export default FrontPage;
